// 音源（base64形式）
const base64 = ""

// Audio オブジェクトを生成
const sound = new Audio("data:audio/wav;base64," + base64);

// タイマー
let taskTimer; let restTimer;
let taskIntervalTime = 20 * 60 * 1000; // 休憩＋作業時間
let restIntervalTime = 18 * 60 * 1000; // 作業時間
let count = 1;

function timerStart() {
    const el = document.querySelector(`ul.list-contents .list-content:nth-of-type(${count}) input`);

    taskTimer = setInterval(taskAram, taskIntervalTime);
    restTimer = setTimeout(restAram, restIntervalTime, el.value);

    alert(`タイマーを開始します。${el.value} を開始してください！`);
    document.querySelector('.time-nowtask-taskname').innerHTML = el.value
    count++;
}

function timerStop() {
    clearTimeout(taskTimer);
    clearTimeout(restTimer);
}

// アラーム
function taskAram() {
    if (count <= document.querySelector('ul.list-contents').childElementCount) {
        const el = document.querySelector(`ul.list-contents .list-content:nth-of-type(${count}) input`);
        count++;
        soundPlay();
        alert(`${el.value} 開始の時間ですよー`);
        document.querySelector('.time-nowtask-taskname').innerHTML = el.value
        restTimer = setTimeout(restAram, restIntervalTime, el.value);
    } else {
        timerStop()
    }
}

function restAram(taskName) {
    soundPlay();
    alert(`${taskName} 終了の時間ですよー`);
}

function soundPlay() {
    for (var i = 0; i <= 5; i++) {
        const t = i * 400
        setTimeout(s, t)
    }
}

function s() {
    sound.play();
}



// 時刻表示
window.onload = function() {
    timeUpdate();
    setInterval(timeUpdate, 1000);
}
function timeUpdate() {
    const nowTime = new Date();
    const nowTimeH = (function(){
        if(nowTime.getHours() < 10) {
            return '0' + nowTime.getHours()
        } else {
            return nowTime.getHours();
        }
    })()
    const nowTimeM = (function() {
        if(nowTime.getMinutes() < 10) {
            return '0' + nowTime.getMinutes()
        } else {
            return nowTime.getMinutes();
        }
    })()
    document.querySelector('.time-nowtime').innerHTML = `${nowTimeH}:${nowTimeM}`
}
